import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import "./App.css";
import FraudCsvAnalyzer from "./FraudCSVAnalyzer";
import LoginPage from "./LoginPage";
import HelpPage from "./HelpPage";

function App() {
  const [user, setUser] = useState(null);

  const handleLogout = () => setUser(null);

  return (
    <Router>
      <div className="min-h-screen bg-gray-100">
        {user && (
          <nav className="flex justify-between items-center bg-blue-600 text-white px-6 py-3 shadow-md">
            <div className="flex items-center gap-6">
              <Link to="/" className="font-semibold hover:underline">
                Analyzer
              </Link>
              <Link to="/help" className="font-semibold hover:underline">
                Help
              </Link>
            </div>
            <div className="flex items-center gap-4">
              <span>{user.email}</span>
              <button
                onClick={handleLogout}
                className="bg-red-500 px-3 py-1 rounded hover:bg-red-600 transition"
              >
                Logout
              </button>
            </div>
          </nav>
        )}

        <div className="p-6">
          <Routes>
            {!user && <Route path="*" element={<LoginPage onLogin={setUser} />} />}
            {user && (
              <>
                <Route path="/" element={<FraudCsvAnalyzer />} />
                <Route path="/help" element={<HelpPage />} />
                <Route path="*" element={<Navigate to="/" />} />
              </>
            )}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
